$scope.qty_per_dzn = Math.round($scope.order.order_qty/12*100, 2)/100;
$scope.total_fob = $scope.order.order_qty * $scope.order.order_fob;
$scope.total_accessories_cost = Math.round(($scope.order.qty_per_dzn * $scope.order.accessories_rate)*100)/100;
$scope.total_button_cost = Math.round(($scope.order.qty_per_dzn * $scope.order.button_rate)*100)/100;
$scope.total_zipper_cost = Math.round(($scope.order.qty_per_dzn * $scope.order.zipper_rate)*100)/100;
$scope.total_print_cost = Math.round(($scope.order.qty_per_dzn * $scope.order.print_rate)*100)/100;
$scope.total_security_tag_cost = Math.round(($scope.qty_per_dzn * $scope.security_tag)*100)/100;
$scope.total_cost =  Math.round(($scope.order.total_yarn_cost + $scope.order.total_accessories_cost + $scope.order.total_button_cost + $scope.order.total_zipper_cost + $scope.order.total_print_cost + $scope.order.total_security_tag_cost)*100)/100;
$scope.order_balance_amount = Math.round(($scope.order.total_fob - $scope.order.total_cost)*100)/100;
$scope.cost_of_making = Math.round(($scope.order.order_balance_amount/$scope.order.qty_per_dzn)*100)/100;